$(function(){var b=$("form.plugin-filtering-widget");b.on("change","input.mass-change:checkbox",function(a){$(a.currentTarget).closest("li").find("ul input:checkbox").prop("checked",a.currentTarget.checked)});b.on("click","span.toggle-nested",function(a){$(a.currentTarget).next("ul").toggle()});b.find("div.slider").each(function(){var a=Math.floor(parseFloat($(this).data("min"))),b=Math.ceil(parseFloat($(this).data("max")));$(this).slider({range:!0,min:a,max:b,values:[a,b]})})});
